package burp;

import java.io.File;
import java.io.FileOutputStream;
import java.util.Base64;

public class GenerateImage {
	static String base64ImageData = "";
	
	
	public static void getcreateImageData() {
		File file = new File(System.getProperty("java.io.tmpdir")+"/"+"burppreviewpanel"+".png");

	    try ( FileOutputStream fos = new FileOutputStream(file); ) {
	      String b64 = base64ImageData;
	      byte[] decoder = Base64.getDecoder().decode(b64);
	      fos.write(decoder);
	      
	    } 
	    catch (Exception e) {
	    	BurpExtender.callbacks.printOutput("Exception: Preview panel image creation");
	    }
	}
}
